import { html } from "@open-wc/testing";
import { runComponentTests } from "../../test/test-utils";
import "../../index";

const base64Image =
    "data:image/png;base64,";

const getChild = (child?: string): string => {
    const srEl = `<span class="v-visible-sr">Stack Overflow</span>`;
    switch (child) {
        case "image":
            return `<img
                class="s-avatar--image"
                src="${base64Image}"
                alt="team logo"
            />${srEl}`;
        case "letter":
            return `<div
                class="s-avatar--letter"
                aria-hidden="true">
                    S
            </div>${srEl}`;
        default:
            return srEl;
    }
};

describe("avatar", () => {
    runComponentTests({
        type: "visual",
        baseClass: "s-avatar",
        variants: ["24", "32", "48", "64", "96", "128"],
        children: {
            default: getChild(),
            image: getChild("image"),
            letter: getChild("letter"),
        },
        attributes: {
            href: "#",
        },
        tag: "a",
        template: ({ component, testid }) => html`
            <div
                data-testid="${testid}"
                class="d-inline-flex ai-center jc-center hmn1 wmn1 p8"
            >
                ${component}
            </div>
        `,
        skippedTestids: [
            /-letter/, // TODO: resolve font-family thrashing issues
        ],
    });
});
